import React, { useState, useRef, useEffect } from "react";
import Card from "../components/ui/Card";
import classes from "../components/beer/NewBeerForm.module.css";

// Start with an upper or lowercase letter. Then followed by any letter or number. Min length 4 max len 24
const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
//   1 upper, 1 lower, 1 digit, 1 special char
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

export default function SignUp() {
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('')

  function submitHandler(event) {
    event.preventDefault();
  }

  return (
    <Card>
      <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor="name">Beer Name</label>
          <input type="text" required id="title" ref={nameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="image">Beer Image</label>
          <input type="url" required id="image" ref={imageInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="address">Brewer Address</label>
          <input type="text" required id="address" ref={addressInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="description">Tasting Notes</label>
          <textarea
            id="tastingNotes"
            required
            rows="5"
            ref={tastingNotesInputRef}
          ></textarea>
        </div>

        <div className={classes.actions}>
          <button>Add Beer</button>
        </div>
      </form>
    </Card>
  );
}
