import React, { useState, useRef, useEffect } from "react";
import Card from "../components/ui/Card";
import classes from "../components/beer/NewBeerForm.module.css";

// Start with an upper or lowercase letter. Then followed by any letter or number. Min length 4 max len 24
const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
//   1 upper, 1 lower, 1 digit, 1 special char
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

export default function SignUp() {
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState("");
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [pwd, setPwd] = useState("");
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState("");
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);

  //   Used to set focus when page forst loads
  useEffect(() => {
    userRef.current.focus();
  }, []);

  //   Test the username being entered against our regex to see if it is valid
  useEffect(() => {
    setValidName(USER_REGEX.test(user));
  }, [user]);

  //   Test the password being entered against our regex to see if it is valid
  //   Also test if out match password matches the original password
  useEffect(() => {
    setValidPwd(PWD_REGEX.test(pwd));
    setValidMatch(pwd === matchPwd);
  }, [pwd, matchPwd]);

  function submitHandler(event) {
    event.preventDefault();
  }


  useEffect(() => {
    setErrMsg('')
  }, [user, pwd, matchPwd]);

  function submitHandler(event) {
    event.preventDefault();
  }

  return (
    <Card>
      <form className={classes.form} onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor="username">Username:</label>
          <input type="text" required id="username" ref={user} />
        </div>
        <div className={classes.control}>
          <label htmlFor="password">Password:</label>
          <input type="password" required id="password" ref={pwd} />
        </div>
        <div className={classes.control}>
          <label htmlFor="match">Confirm Password:</label>
          <input type="text" required id="match" ref={matchPwd} />
        </div>
        <div className={classes.control}>
          <label htmlFor="description">Tasting Notes</label>
          <textarea
            id="tastingNotes"
            required
            rows="5"
            ref={tastingNotesInputRef}
          ></textarea>
        </div>

        <div className={classes.actions}>
          <button>Add Beer</button>
        </div>
      </form>
    </Card>
  );
}
